package NeebalgurukulDay3;

public class Pattern {

	public static void main(String[] args) {
		
				
	    for (int i=1;i<=5;i++) {
	    	
		    for (int j=1;j<=i;j++) {
		    	
		    
		      if(i%2==0)
		    	  
		    	System.out.print("* ");
		    	else
		    		System.out.print((char)(j+64)+" ");
		    	
		    }
		
		    System.out.println();
		// TODO Auto-generated method stub
	    }
	}}


